import { useState, useEffect } from "react";
import ParticleCanvas from "./components/ParticleCanvas";

export default function App() {
  const [stage, setStage] = useState("bigbang");
  const [paused, setPaused] = useState(false);

  // 단계 자동 전환
  useEffect(() => {
    if (paused) return;

    let timer;
    if (stage === "bigbang") timer = setTimeout(() => setStage("atom_formation"), 5000);
    else if (stage === "atom_formation") timer = setTimeout(() => setStage("galaxy_formation"), 5000);
    else if (stage === "galaxy_formation") timer = setTimeout(() => setStage("solar_system"), 5000);

    return () => clearTimeout(timer);
  }, [stage, paused]);

  return (
    <div>
      <div style={{ position: "fixed", top: 10, left: 10, zIndex: 10 }}>
        <p>Stage: {stage}</p>
        <button onClick={() => setPaused(!paused)}>
          {paused ? "Resume" : "Pause"}
        </button>
      </div>
      <ParticleCanvas stage={stage} paused={paused} />
    </div>
  );
}
