// src/hooks/useSimulation.js
import { useState, useEffect } from "react";
import axios from "axios";

export function useSimulation(userid) {
  const [stage, setStage] = useState("bigbang");
  const [status, setStatus] = useState("paused");

  const fetchStage = async () => {
    if (!userid) return;
    try {
      const res = await axios.get(`http://localhost:4000/api/simulation/stage/${userid}`);
      setStage(res.data.stage);
    } catch (err) {
      console.error("Stage fetch error:", err.message);
    }
  };

  const startSimulation = async () => {
    if (!userid) return;
    try {
      const res = await axios.post("http://localhost:4000/api/simulation/start", { userid });
      setStatus(res.data.status);
      setStage(res.data.stage);
    } catch (err) {
      console.error("Start error:", err.message);
    }
  };

  const pauseSimulation = async () => {
    if (!userid) return;
    try {
      const res = await axios.post("http://localhost:4000/api/simulation/pause", { userid });
      setStatus(res.data.status);
    } catch (err) {
      console.error("Pause error:", err.message);
    }
  };

  useEffect(() => {
    if (!userid) return;
    const interval = setInterval(fetchStage, 1000);
    return () => clearInterval(interval);
  }, [userid]);

  return { stage, status, startSimulation, pauseSimulation };
}
