// src/App.jsx
import { useState, useEffect } from "react";
import axios from "axios";
import ParticleCanvas from "./components/ParticleCanvas";
import { useSimulation } from "./hooks/useSimulation";

function App() {
  const [userId, setUserId] = useState(null);

  // 유저 생성
  useEffect(() => {
    const createUser = async () => {
      try {
        const res = await axios.post("http://localhost:4000/api/user", { username: "홍길동" });
        setUserId(res.data.userid);
      } catch (err) {
        console.error("User creation error:", err.message);
      }
    };
    createUser();
  }, []);

  const { stage, status, startSimulation, pauseSimulation } = useSimulation(userId);

  return (
    <div>
      <h1>BigBang Simulation</h1>
      <p>UserID: {userId || "생성 중..."}</p>
      <p>Stage: {stage} | Status: {status}</p>
      <div style={{ marginBottom: "10px" }}>
        <button onClick={startSimulation} disabled={!userId || status === "started"}>Start</button>
        <button onClick={pauseSimulation} disabled={!userId || status === "paused"}>Pause</button>
      </div>
      <ParticleCanvas userid={userId} stage={stage} />
    </div>
  );
}

export default App;
