import { useRef, useEffect, useState } from "react";
import * as THREE from "three";
import { CSS2DRenderer } from "three/examples/jsm/renderers/CSS2DRenderer";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";
import axios from "axios";
import { useSimulation } from "../hooks/useSimulation";
import { createSun, createPlanets } from "../utils/solarSystem";

export default function ParticleCanvas() {
  const mountRef = useRef();
  const [isPaused, setIsPaused] = useState(false);
  const [userid, setUserid] = useState(null); // 서버에서 받은 userid

  // 사용자 생성
  useEffect(() => {
    axios.post("http://localhost:4000/api/user", { username: "testuser" })
      .then(res => setUserid(res.data.userid))
      .catch(err => console.error(err));
  }, []);

  // useSimulation 훅 연결
  const { stage, status, startSimulation, pauseSimulation } = useSimulation(userid);

  useEffect(() => {
    if (!userid) return; // userid 없으면 초기화하지 않음
    const mount = mountRef.current;
    if (!mount) return;

    const width = mount.clientWidth;
    const height = mount.clientHeight;

    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x000000);

    const camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 5000);
    camera.position.z = 600;

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(width, height);
    mount.appendChild(renderer.domElement);

    const labelRenderer = new CSS2DRenderer();
    labelRenderer.setSize(width, height);
    labelRenderer.domElement.style.position = "absolute";
    labelRenderer.domElement.style.top = "0";
    mount.appendChild(labelRenderer.domElement);

    const controls = new OrbitControls(camera, labelRenderer.domElement);
    controls.enableDamping = true;
    controls.enablePan = false;

    // Lights
    const pointLight = new THREE.PointLight(0xffffff, 3, 5000);
    pointLight.position.set(0, 0, 0);
    scene.add(pointLight);
    scene.add(new THREE.AmbientLight(0xffffff, 0.4));

    let points = null;
    let sun = null;
    let planets = [];

    const setupStage = () => {
      const removable = scene.children.filter(
        obj => obj.userData.isPlanet || obj.userData.isSun || obj.userData.isOrbit || obj.userData.isBigBang || obj.userData.isGalaxy
      );
      removable.forEach(obj => scene.remove(obj));

      points = null;
      sun = null;
      planets = [];

      if (stage === "bigbang" || stage === "atom_formation") {
        const numParticles = 8000;
        const geometry = new THREE.BufferGeometry();
        const positions = new Float32Array(numParticles * 3);
        const colors = new Float32Array(numParticles * 3);
        const color = new THREE.Color();

        for (let i = 0; i < numParticles; i++) {
          positions[i * 3] = (Math.random() - 0.5) * 800;
          positions[i * 3 + 1] = (Math.random() - 0.5) * 800;
          positions[i * 3 + 2] = (Math.random() - 0.5) * 800;
          const c = Math.random();
          color.setRGB(c, c, c);
          colors[i * 3] = color.r;
          colors[i * 3 + 1] = color.g;
          colors[i * 3 + 2] = color.b;
        }

        geometry.setAttribute("position", new THREE.BufferAttribute(positions, 3));
        geometry.setAttribute("color", new THREE.BufferAttribute(colors, 3));
        points = new THREE.Points(geometry, new THREE.PointsMaterial({ size: 2, vertexColors: true }));
        points.userData.isBigBang = true;
        scene.add(points);
      }

      if (stage === "galaxy_formation") {
        const { generateGalaxy } = require("../utils/galaxyUtils");
        points = generateGalaxy(5000);
        points.userData.isGalaxy = true;
        scene.add(points);
      }

      if (stage === "solar_system") {
        sun = createSun();
        scene.add(sun);
        planets = createPlanets(scene);
      }
    };

    setupStage();

    let frameId;
    const animate = () => {
      if (!isPaused && status !== "paused") {
        if (stage === "solar_system" && sun) {
          sun.rotation.y += 0.002;
          planets.forEach(p => {
            p.angle += p.speed;
            p.mesh.position.x = p.radius * Math.cos(p.angle);
            p.mesh.position.z = p.radius * Math.sin(p.angle);
          });
        } else if (points) {
          points.rotation.z += 0.001;
        }
      }

      controls.update();
      renderer.render(scene, camera);
      labelRenderer.render(scene, camera);
      frameId = requestAnimationFrame(animate);
    };
    animate();

    return () => {
      cancelAnimationFrame(frameId);
      renderer.dispose();
      mount.removeChild(renderer.domElement);
      mount.removeChild(labelRenderer.domElement);
    };
  }, [stage, isPaused, status, userid]);

  return (
    <div style={{ position: "relative", width: "100%", height: "600px" }}>
      <div ref={mountRef} style={{ width: "100%", height: "100%" }} />

      <div style={{ position: "absolute", top: 10, left: 10, zIndex: 10, display: "flex", gap: "8px" }}>
        <button onClick={startSimulation} disabled={!userid}>Start</button>
        <button onClick={pauseSimulation} disabled={!userid}>{status === "paused" ? "Resume" : "Pause"}</button>
      </div>
    </div>
  );
}
