import * as THREE from "three";

export function generateGalaxy(numParticles = 2500) {
  const geometry = new THREE.BufferGeometry();
  const positions = new Float32Array(numParticles * 3);
  const colors = new Float32Array(numParticles * 3);
  const color = new THREE.Color();

  const arms = 2;
  const armTwist = 0.5;

  for (let i = 0; i < numParticles; i++) {
    const r = Math.random() ** 0.5;
    const angle = r * 2 * Math.PI * arms + Math.random() * armTwist;

    positions[i * 3] = r * Math.cos(angle) * 300;
    positions[i * 3 + 1] = r * Math.sin(angle) * 300;
    positions[i * 3 + 2] = (Math.random() - 0.5) * 100;

    const c = 0.6 + Math.random() * 0.4;
    color.setRGB(c, c, 1);
    colors[i * 3] = color.r;
    colors[i * 3 + 1] = color.g;
    colors[i * 3 + 2] = color.b;
  }

  geometry.setAttribute("position", new THREE.BufferAttribute(positions, 3));
  geometry.setAttribute("color", new THREE.BufferAttribute(colors, 3));

  const material = new THREE.PointsMaterial({ size: 2, vertexColors: true });
  return new THREE.Points(geometry, material);
}
