import { useState, useEffect, useRef } from "react";
import axios from "axios";
import ParticleCanvas from "./components/ParticleCanvas";
import { io } from "socket.io-client";

export default function App() {
  const [userid, setUserId] = useState(null);
  const [stage, setStage] = useState("bigbang");
  const [status, setStatus] = useState("paused");

  const socketRef = useRef();

  // ------------------------
  // 접속 시 사용자 생성
  // ------------------------
  useEffect(() => {
    const initUser = async () => {
      try {
        const res = await axios.post("http://localhost:4000/api/user", {
          username: "Guest",
        });
        setUserId(res.data.userid);

        // WebSocket 연결
        socketRef.current = io("http://localhost:4000");
        socketRef.current.emit("join", res.data.userid);

        // 서버 상태 수신
        socketRef.current.on("updateStage", (data) => {
          setStage(data.stage);
          setStatus(data.status);
        });
      } catch (err) {
        console.error("사용자 생성 실패:", err);
      }
    };
    initUser();
  }, []);

  // ------------------------
  // 시뮬레이션 제어
  // ------------------------

  const pauseSimulation = async () => {
    if (!userid) return;
    const res = await axios.post("http://localhost:4000/api/simulation/pause", { userid });
    setStatus(res.data.status);
    setStage(res.data.stage);
    socketRef.current.emit("pause", { userid });
  };

  const changeStage = async (targetStage) => {
  if (!userid) return;
  try {
    let endpoint = "";
    const order = ["bigbang", "galaxy", "solar_system"];
    const currentIndex = order.indexOf(stage);

    if (targetStage === "bigbang") {
      endpoint = currentIndex > 0 ? `/api/simulation/prev/${userid}` : "";
    } else if (targetStage === "galaxy") {
      endpoint = currentIndex < 1 ? `/api/simulation/next/${userid}` : (currentIndex > 1 ? `/api/simulation/prev/${userid}` : "");
    } else if (targetStage === "solar_system") {
      endpoint = currentIndex < 2 ? `/api/simulation/next/${userid}` : "";
    }

    if (!endpoint) return;

    const res = await axios.post(`http://localhost:4000${endpoint}`);
    setStage(res.data.stage);
    setStatus(res.data.status || "started");
  } catch (err) {
    console.error(err);
  }
};



  return (
    <div>
      <h1>BigBang Simulator</h1>
      <ParticleCanvas
        userid={userid}
        stage={stage}
        status={status}
        pauseSimulation={pauseSimulation}
        changeStage={changeStage}
      />
    </div>
  );
}
