// src/App.jsx
import { useState, useEffect } from "react";
import axios from "axios";
import ParticleCanvas from "./components/ParticleCanvas";

export default function App() {
  const [userid, setUserId] = useState(null);
  const [stage, setStage] = useState("bigbang");
  const [status, setStatus] = useState("paused");

  // ------------------------
  // 접속 시 사용자 생성
  // ------------------------
  useEffect(() => {
    const initUser = async () => {
      try {
        const res = await axios.post("http://localhost:4000/api/user", {
          username: "Guest",
        });
        setUserId(res.data.userid);
        setStage("bigbang");
        setStatus("paused");
      } catch (err) {
        console.error("사용자 생성 실패:", err);
      }
    };
    initUser();
  }, []);

  // ------------------------
  // 서버에서 stage 가져오기
  // ------------------------
  const fetchStage = async () => {
    if (!userid) return;
    try {
      const res = await axios.get(
        `http://localhost:4000/api/simulation/stage/${userid}`
      );
      setStage(res.data.stage);
    } catch (err) {
      console.error("stage 조회 실패:", err);
    }
  };

  useEffect(() => {
    if (userid) fetchStage();
  }, [userid]);

  // ------------------------
  // 시뮬레이션 상태 제어
  // ------------------------
  const startSimulation = async () => {
    if (!userid) return;
    try {
      await axios.post("http://localhost:4000/api/simulation/start", { userid });
      setStatus("started");
      setStage("bigbang");
    } catch (err) {
      console.error(err);
    }
  };

  const pauseSimulation = async () => {
    if (!userid) return;
    try {
      const res = await axios.post("http://localhost:4000/api/simulation/pause", {
        userid,
      });
      setStatus(res.data.status);
      setStage(res.data.stage);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div>
      <h1>BigBang Simulator</h1>
      <ParticleCanvas
        userid={userid}
        stage={stage}
        status={status}
        startSimulation={startSimulation}
        pauseSimulation={pauseSimulation}
      />
    </div>
  );
}
